package com.agmtopt.leetcode.algorithmtrainingcamp.strings;

public class ValidPalindrome2_680 {

  public static void main(String[] args) {
    ValidPalindrome2_680_Solution solution = new ValidPalindrome2_680_Solution();
    String s = "";
    boolean result = solution.validPalindrome(s);

    System.out.println("result:" + result);
  }
}

class ValidPalindrome2_680_Solution {

  public boolean validPalindrome(String s) {
    int low = 0, high = s.length() - 1;

    while (low < high) {
      char c1 = s.charAt(low), c2 = s.charAt(high);
      if (c1 == c2) {
        low++;
        high--;
      } else {
        return isValidPalindrome(s, low+1, high) || isValidPalindrome(s, low, high-1);
      }
    }
    return true;
  }

  private boolean isValidPalindrome(String s, int low, int high) {
    int left = low;
    int right = high;

    char[] array = s.toCharArray();

    while (left <= right) {

      if (array[left] == array[right]) {
        left++;
        right--;
        continue;
      }
      return false;
    }

    return true;
  }

}
